function calculateAndSort(){var t=document.getElementById("shanyraqTable"),e=Array.from(t.querySelectorAll("tbody tr"));e.forEach(t=>{var t=t.querySelectorAll("td"),e=Array.from(t).slice(0,4).map(t=>parseInt(t.textContent||"0")).reduce((t,e)=>t+e,0);t[4].textContent=e}),e.sort((t,e)=>{t=parseInt(t.querySelector("td:last-child").textContent||"0");return parseInt(e.querySelector("td:last-child").textContent||"0")-t});const n=t.querySelector("tbody");e.forEach(t=>n.appendChild(t))}document.addEventListener("DOMContentLoaded",function(){const e=document.querySelectorAll(".rating-nav a"),n=document.querySelectorAll(".rating-section");e.forEach(t=>{t.addEventListener("click",function(t){t.preventDefault(),e.forEach(t=>t.classList.remove("active")),n.forEach(t=>t.classList.remove("active")),this.classList.add("active");t=this.getAttribute("data-rating");document.getElementById(t).classList.add("active")})})}),window.addEventListener("DOMContentLoaded",calculateAndSort);